import React, { Component } from 'react';
import '../css/HeaderBar.css';
import '../css/ColorPicker.css';
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStroopwafel } from '@fortawesome/free-solid-svg-icons';
import { faEyeDropper} from '@fortawesome/free-solid-svg-icons';
import NumericInput from 'react-numeric-input';



library.add(faStroopwafel);
library.add(faEyeDropper);


class ColorPicker extends Component {
    constructor(){
      super();
      this.state = {

      }
    }

    // var style = {
    //     backgroundColor : {currentColor}  
    //   };

    DisplayColors(){

        let colorBoxes= []

        // Loop through all colors.
        for (let i = 0; i < 10000; i++) {

            i = i + 10;

            // Convert integer value to hex.
            var hexCode = i.toString(16);            
            var currentColor = '#';

            // Check if color hexcode is smaller than 6 and needs to be filled with 0's.
            if (hexCode.length < 6){

                for (var y=0; y < 6 - hexCode.length; y++){
                    currentColor += '0';
                }
                currentColor += hexCode;
                currentColor = currentColor.toUpperCase();
            }

          //console.log(currentColor);

          colorBoxes.push(<div key={i} className="colorBox" style={{backgroundColor: `${currentColor}`}}></div>)

        }
        return(
            <div className="colorBoxContainer">{colorBoxes}</div>
        )
    }


    render() {
      return (        
        <div>
           <h1>Color picker</h1>
           <br></br>

           <div className="container">
            <div className="form-row">
                <div className = "col-2">
                    Red</div>
                <div className="col-1">
                    Red <NumericInput min={0} max={100} value={50}/>  
                    Green &nbsp;<NumericInput min={0} max={100} value={50}/>
                    Blue &nbsp;<NumericInput min={0} max={100} value={50}/>
                </div>
                <div className="col-4">

                </div>
                <div className="col-4">

                </div>
            </div>

           </div>

           <br></br>

           <br></br>
           Blue &nbsp;<NumericInput min={0} max={100} value={50}/>
           <br></br>
           <div class="slidecontainer">
                <input type="range" min="1" max="100" value="50" class="slider" id="myRange"></input>
            </div>
           <br></br>
           {this.DisplayColors()}
        </div>
      );
    }
  }

  export default ColorPicker;
