import React, { Component } from 'react';

import '../css/bootstrap.min.css';
import '../css/HeaderBar.css';
import '../css/ColorPicker.css';
import '../css/ColorSlider.css';

import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStroopwafel } from '@fortawesome/free-solid-svg-icons';
import { faEyeDropper} from '@fortawesome/free-solid-svg-icons';
import { faPlusCircle } from '@fortawesome/free-solid-svg-icons';
import { faMinusCircle } from '@fortawesome/free-solid-svg-icons';



library.add(faStroopwafel);
library.add(faEyeDropper);
library.add(faPlusCircle);
library.add(faMinusCircle);


class ColorPicker extends Component {
    constructor(){
      super();

      this.state = { 
          favoriteColors: [],
          colorHexCode: 0,
          red: 0,
          green: 0,
          blue: 0,
        };

    }
    updateRed(event) {

        this.setState({
          red: event.target.value
        });

        return true;
    }

    updateGreen(event) {

        this.setState({
          green: event.target.value
        });

        return true;
    }

    updateBlue(event) {

        this.setState({
          blue: event.target.value
        });

        return true;
    }

    // addColor(event) {

    //     // Create 

    //     // Populate html object for color card.
    //     this.state.favoriteColors.push()
    //     // this.setState({
    //     //   red: event.target.value
    //     // });

    //     return true;
    // }


    addFavourite() {  

        var hexColorCode = this.rgb2hex(this.state.red, this.state.green, this.state.blue);

        //Check if selected item is already in array
        if (!this.state.favoriteColors.includes(hexColorCode)){
            //add item to array            
            this.state.favoriteColors.push(hexColorCode);   
        }
          //Item is not in array
        else{

          }
          return true;
    }



    /* 
        Function name: updateSearchInput

        Purpose:    Function is called when the text in the input search box has changed.
                    The value from the event is put into searchInput variable in class state.

        Parameters:     event
        Return:         true
    */
   updateSearchInput(event, colorOrder) {

    // This is red.
    if (colorOrder === 1){


        // Set the red color state.
        this.setState({
            red: event.target.value
        });

    }

    // This is green.
    else if (colorOrder === 2){

        // Set the green color state.
        this.setState({
            green: event.target.value
        });
    }

    // This is blue.
    else if (colorOrder === 3){

        // Set the blue color state.
        this.setState({
            blue: event.target.value
        });
    }



    return true;
}

    componentToHex(c) {
        var hex = c.toString(16);
        return hex.length == 1 ? "0" + hex : hex;
    }

    rgbToHex(r, g, b) {        

        return "#" + this.componentToHex(r) + this.componentToHex(g) + this.componentToHex(b);
  }

  //alert(rgbToHex(0, 51, 255)); // #0033ff

    rgb2hex(red, green, blue) {
        var rgb = blue | (green << 8) | (red << 16);

        var returnString = '#' + (0x1000000 + rgb).toString(16).slice(1);        

        return '#' + (0x1000000 + rgb).toString(16).slice(1);
    }    

    renderColorHexCode(){

        var hexColorCode = this.rgb2hex(this.state.red, this.state.green, this.state.blue);

        return (<div><h1>{hexColorCode}</h1></div>)
    }

    renderFavoriteColors(){


        return (<div>

            {this.state.favoriteColors.map(currentColor=>            
            <p>
                {currentColor}
            </p>
            )}

        </div>


        // <div>
        //     {currentFavoriteColor}
        //     </div>                                                        


        // </div>
        )
    }


    DisplayColors(){

        let colorBoxes= []

        // Loop through all colors.
        for (let i = 0; i < 10000; i++) {

            i = i + 10;

            // Convert integer value to hex.
            var hexCode = i.toString(16);            
            var currentColor = '#';

            // Check if color hexcode is smaller than 6 and needs to be filled with 0's.
            if (hexCode.length < 6){

                for (var y=0; y < 6 - hexCode.length; y++){
                    currentColor += '0';
                }
                currentColor += hexCode;
                currentColor = currentColor.toUpperCase();
            }          

          colorBoxes.push(<div key={i} className="colorBox" style={{backgroundColor: `${currentColor}`}}></div>)

        }
        return(
            <div className="colorBoxContainer">{colorBoxes}</div>
        )
